mapper.select:
SELECT 

FROM wemall_freight_info a

WHERE a.id = #{id}

SELECT
FROM wemall_freight_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.freight_no = #{freightNo}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.cur_place LIKE '%'||#{curPlace}||'%' '%'+#{curPlace}+'%' concat('%',#{curPlace},'%')
AND a.send_people LIKE '%'||#{sendPeople}||'%' '%'+#{sendPeople}+'%' concat('%',#{sendPeople},'%')
ORDER BY ${page.orderBy}

SELECT 

FROM wemall_freight_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE wemall_freight_info SET 	
	freight_no = #{freightNo},
	name = #{name},
	cur_place = #{curPlace},
	send_people = #{sendPeople}
WHERE id = #{id}

UPDATE wemall_freight_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
