mapper.select:
SELECT 

FROM wemall_item_sort a

WHERE a.id = #{id}

SELECT
FROM wemall_item_sort a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM wemall_item_sort a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE wemall_item_sort SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE wemall_item_sort SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
