SELECT 

FROM wemall_shop_car a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.user_id = #{user.id}


		AND a.item_id = #{itemId}


		AND i.name LIKE 
			'%'||#{item.name}||'%'
			'%'+#{item.name}+'%'
			concat('%',#{item.name},'%')


		AND a.item_num = #{itemNum}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wemall_shop_car a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


