mapper.select:
SELECT 

FROM wemall_spec_info a

WHERE a.id = #{id}

SELECT 

FROM wemall_spec_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.spec_id = #{specId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wemall_spec_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wemall_spec_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
