mapper.select:
SELECT 

FROM con_activity a

WHERE a.id = #{id}

SELECT 

FROM con_activity a

WHERE a.del_flag = 0 
	and a.fromdate <= #{date} 
	and a.enddate >= #{date}

SELECT 

FROM con_activity a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.organizer LIKE 
			'%'||#{organizer}||'%'
			'%'+#{organizer}+'%'
			concat('%',#{organizer},'%')


		AND a.fromdate BETWEEN #{beginFromdate} AND #{endFromdate}


		AND a.enddate BETWEEN #{beginEnddate} AND #{endEnddate}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM con_activity a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE con_activity SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
