mapper.select:
SELECT 

FROM wx_user_info a

WHERE a.id = #{id}

SELECT 

FROM wx_user_info a

WHERE a.user_id = #{userId}

SELECT 

FROM wx_user_info a

WHERE a.open_id = #{openId}

SELECT 

FROM wx_user_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.user_id = #{user.id}


		AND a.service_id = #{serviceId}


		AND a.open_id LIKE 
			'%'||#{openId}||'%'
			'%'+#{openId}+'%'
			concat('%',#{openId},'%')


		AND a.user_name LIKE 
			'%'||#{userName}||'%'
			'%'+#{userName}+'%'
			concat('%',#{userName},'%')


		AND a.nick_name LIKE 
			'%'||#{nickName}||'%'
			'%'+#{nickName}+'%'
			concat('%',#{nickName},'%')


		AND a.mobile LIKE 
			'%'||#{mobile}||'%'
			'%'+#{mobile}+'%'
			concat('%',#{mobile},'%')


		AND a.is_focus = #{isFocus}


		AND a.is_get_user_info = #{isGetUserInfo}


		AND a.is_change_pw = #{isChangePw}


		AND a.is_bind_mobile = #{isBindMobile}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_user_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_user_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
