mapper.select:
SELECT 

FROM wx_userinfo a

WHERE a.wx_userid = #{wxUserId}

SELECT
FROM wx_userinfo a
WHERE a.del_flag = 0 and a.subscriberid = #{subscriberId}

SELECT
FROM wx_userinfo a
WHERE a.del_flag = 0 and a.sl_userid = #{sysUserId}

SELECT
FROM wx_userinfo a
WHERE a.del_flag = 0 and a.openid = #{openId}

SELECT
FROM wx_userinfo a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.wx_no LIKE '%'||#{wxNo}||'%' '%'+#{wxNo}+'%' concat('%',#{wxNo},'%')
AND a.mobile LIKE '%'||#{mobile}||'%' '%'+#{mobile}+'%' concat('%',#{mobile},'%')
AND a.username LIKE '%'||#{username}||'%' '%'+#{username}+'%' concat('%',#{username},'%')
AND a.nickname LIKE '%'||#{nickname}||'%' '%'+#{nickname}+'%' concat('%',#{nickname},'%')
AND a.sex = #{sex}
AND a.sl_userid = #{slUser.id}
AND a.status = #{status}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM wx_userinfo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_userinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE wx_userid = #{wxUserId}
